mapper.select:

SELECT COUNT(*) 
FROM board 
WHERE title like #{kwd} OR content like #{kwd}

mapper.update:

UPDATE board AS b 
SET b.title=#{title}, b.content=#{content}, b.reg_date=now() 
WHERE b.no=#{no} AND b.user_no= #{user_no}

mapper.select:

SELECT MAX(depth) 
FROM board 
WHERE group_no = #{group_no}

mapper.delete:
DELETE 
FROM board  
WHERE no= #{no} And user_no = #{user_no} AND depth = #{depth}
